#include <stdio.h>
#include <stdlib.h>

#include <libibur/test.h>
#include <libibur/util.h>

#include <sha256.h>

void sha256Test() {
	const char* in[] = {
		"",
		"",
		"",
		"",
		"",
		""
	};

	const char* out[] = {
		"e3b0c44298fc1c149afbf4c8996fb92427ae41e4649b934ca495991b7852b855",
		"3c593aa539fdcdae516cdf2f15000f6634185c88f505b39775fb9ab137a10aa2",
		"46500b6ae1ab40bde097ef168b0f3199049b55545a1588792d39d594f493dca7",
		"5f4e16a72d6c9857da0ba009ccacd4f26d7f6bf6c1b78a2ed35e68fcb15b8e40",
		"33b6229592ca719e4e46f35b287617fedadd3b7c38be3c8c1c9f446d2d9085b3",
		"90df9cc3a3b904415331eba9cd52750c2c5cb73cb91b42caca7eee3788fc2b30"
	};

	const uint64_t len[] = {
		0/8,
		1304/8,
		2096/8,
		2888/8,
		51200/8,
		50408/8
	};
	for(int i = 0; i < (sizeof(len)/sizeof(len[0])); i++) {
		uint8_t* input = (uint8_t*) malloc(len[i]);
		uint8_t* output = (uint8_t*) malloc(32);
		uint8_t* expected_out = (uint8_t*) malloc(32);
		from_hex(in[i], input);
		from_hex(out[i], expected_out);
		sha256(input, len[i], output);
		assert_equals(output, expected_out, 32, "sha256 test");
		free(input);
		free(output);
		free(expected_out);
	}
}



void pbkdf_hmac_sha256Test() {
	const char* pass[] = {
		"password",
		"password",
		"password",
		"passwordPASSWORDpassword",
		"passwordPASSWORDpassword"
	};
	const uint64_t plen[] = {
		8,
		8,
		8,
		24,
		24
	};
	const char* salt[] = {
		"salt",
		"salt",
		"salt",
		"saltSALTsaltSALTsaltSALTsaltSALTsalt",
		"saltSALTsaltSALTsaltSALTsaltSALTsalt"
	};
	const uint64_t slen[] = {
		4,
		4,
		4,
		36,
		36
	};
	const uint64_t c[] = {
		1,
		2,
		4096,
		4096,
		65536
	};
	const char* out[] = {
		"12 0f b6 cf fc f8 b3 2c\
		 43 e7 22 52 56 c4 f8 37\
		 a8 65 48 c9 2c cc 35 48\
		 08 05 98 7c b7 0b e1 7b",
		"ae 4d 0c 95 af 6b 46 d3\
		 2d 0a df f9 28 f0 6d d0\
		 2a 30 3f 8e f3 c2 51 df\
		 d6 e2 d8 5a 95 47 4c 43",
		"c5 e4 78 d5 92 88 c8 41\
		 aa 53 0d b6 84 5c 4c 8d\
		 96 28 93 a0 01 ce 4e 11\
		 a4 96 38 73 aa 98 13 4a",
		"34 8c 89 db cb d3 2b 2f\
		 32 d8 14 b8 11 6e 84 cf\
		 2b 17 34 7e bc 18 00 18\
		 1c 4e 2a 1f b8 dd 53 e1\
		 c6 35 51 8c 7d ac 47 e9",
		"84 4f e8 fa 4d a1 10 b5\
		 e1 d5 5f 02 a5 7a 87 f9\
		 1e 78 10 99 0b 70 f7 79\
		 b9 95 ad ab 41 59 4a 00\
		 0b 8a 7a 63 84 32 fc ab\
		 8f 30 f7 ef f7 0d 05 16\
		 00 37 82 1d 1e 43 6c af\
		 6a a5 39 0b 2b d8 5a 23"
	};
	const uint64_t dklen[] = {
		32,
		32,
		32,
		40,
		64
	};
	
	for(int i = 0; i < (sizeof(plen)/sizeof(plen[0])); i++) {
		uint8_t* exp_out = (uint8_t*) malloc(dklen[i]);
		uint8_t* output = (uint8_t*) malloc(dklen[i]);
		from_hex(out[i], exp_out);
		pbkdf2_hmac_sha256((uint8_t*) pass[i], plen[i], (uint8_t*) salt[i], slen[i], c[i], dklen[i], output);
		
		assert_equals(output, exp_out, dklen[i], "pbkdf2 hmac sha256 test");
		free(output);
		free(exp_out);
	}
}

void sha256_tests() {
	sha256Test();
	pbkdf_hmac_sha256Test();
}
